package com.kishore.java;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// to avoid multiple If stat use SWITCH stat
		int age;
		age = 3;
		
		switch (age){
			case 1:
				System.out.println("It is one");
				break;
			case 2:
				System.out.println("It is two");
				break;
			default:
				System.out.println("It is default");
				break;
					
		}
		
// double clikc before line no, for break point, F^ next line, F5 to step into, 
		//to start Debug click on Bug sign left of execute button above
		// retun next to step into button, also stop red square to stop debugging
	}

}
